export const FilterBuilder = {
  operation: {
    like: "like",
    equals: "equals"
  },
  properties: {
    filed: "field",
    operation: "operation",
    value: "value"
  },
  sparator: "/",
  generateUri
};

function generateUri(filters, count = null, page = null) {
  var uri = "";
  var filter = "";
  filters.forEach(rows => {
    filter = filter.concat(
      (filter !== "" ? "," : "") +
        rows.field +
        FilterBuilder.sparator +
        rows.operation +
        FilterBuilder.sparator +
        rows.value
    );
  });
  //  console.log(filter);
  if (filter !== "") {
    uri = "?" + filter + (count !== null ? "&count=" + count : "") + (page !== null ? "&page=" + page : "");
  } else {
    uri = "?" + (count !== null ? "count=" + count : "") + (page !== null ? +(count !== null ? "&" : "") + "page=" + page : "");
  }
  return filter;
}
