import React from 'react';
import {authenticationService} from '../../service/AutenticationService';

class Login extends React.Component {
    constructor(props) {
        super(props);

        if (authenticationService.currentUserValue) { 
            this.props.history.push('/');
        }

        this.state = {
            user:{
                email:"",
                password:"",
                confirmPassword:""
            },
            submited:false,
            error:{
                errorEmail:'',
                errorPassword:''
            }
      };
      this.handleChange = this.handleChange.bind(this);
      this.Login = this.Login.bind(this);
    }
    componentWillMount(){
        this.setState({submited:false});
    }
    componentDidMount(){
        this.setState({submited:false});
    }
    handleChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        let state = Object.assign({}, this.state);
        state.user[name] = value;
        this.setState(state);
        this.validate();
      }

    Login(event) {
        this.setState({submited: true}, function() {
           if(this.validate()){
                var that = this;
                authenticationService.login(this.state.user).then( result => {
                    this.props.history.push('/');
                });
            }
        });

    }
    validate(){
        var isValid = true;
        var errorMasage = this.state.error;
        if((this.state.user.email == '' || this.state.user.email == undefined || this.state.user.email == null) && this.state.submited){
            errorMasage.errorEmail ='Email is required';

            isValid = false;
        }else{
            errorMasage.errorEmail =null;
        }
        if((this.state.user.password == '' || this.state.user.password == undefined || this.state.user.password == null)  && this.state.submited){
            errorMasage.errorPassword ='Password is required';
            isValid= false;
        }else{
            errorMasage.errorPassword =null;
        }
        this.setState({error:errorMasage});
        return isValid;
    }
    render() {
        return <div className="login">
            <div className="wrapper wrapper-login">
                <div className="container container-login animated fadeIn">
                    <h3 className="text-center">Sign In</h3>
                    <div className="login-form">
                        <div className="form-group">
                            <label htmlFor="email" className="placeholder"><b>Username</b></label>
                            <input id="email" name="email" type="text" className={this.state.error.errorEmail == ''? 'form-control':'form-control is-invalid'} required onChange={this.handleChange.bind(this)}/>
                            <span className='text-danger'>{this.state.error.errorEmail}</span>
                        </div>
                        <div className="form-group">
                            <label htmlFor="password" className="placeholder"><b>Password</b></label>
                            <a href="#" className="link float-right">Forget Password ?</a>
                            <div className="position-relative">
                                <input id="password" name="password" type="password" className={this.state.error.errorPassword == ''?'form-control':'form-control is-invalid'} required onChange={this.handleChange.bind(this)} />
                                <div className="show-password">
                                    <i className="flaticon-interface"></i>
                                </div>
                                <span className='text-danger'>{this.state.error.errorPassword}</span>
                            </div>
                        </div>
                        <div className="form-group form-action-d-flex mb-3">
                            <div className="custom-control custom-checkbox">
                                <input type="checkbox" className="custom-control-input" id="rememberme"/>
                                <label className="custom-control-label m-0" htmlFor="rememberme">Remember Me</label>
                            </div>
                            <button onClick={this.Login.bind(this)}
                            className="btn btn-primary btn-rounded btn-login">Sign In</button>
                        </div>
                        <div className="login-account">
                            <span className="msg">Don't have an account yet ?</span>
                            <a href="#" id="show-signup" className="link">Sign Up</a>
                        </div>
                    </div>
		        </div>
            </div>
        </div>;

    }
}

export default Login;
