import {config} from '../config/Config';
import { handleResponse } from './HandleResponseSevice';
import {httpHeader} from '../config/HttpHeader';
import axios from 'axios';


export const httpService = {
    Post,
    Get,
};

function Post(urlRoute,data) {
    return axios.post(config.apiUrl+urlRoute, data)
    .then(handleResponse).then(respose => {
        return respose;
    }).catch(err => {
        console.log(err);
    });
}

function Get(urlRoute,param) {
    axios.defaults.baseURL = config.HeaderRequest.baseURL;
    axios.defaults.headers.common['Authorization'] = "Bearer "+currentUser.token;
    axios.defaults.headers.post['Content-Type'] = config.HeaderRequest.ContentType;
    return axios.get("http://127.0.0.1:8000/api/user/users")
    .catch(err => {
        console.log(err);
    });
}
