    constructor(props) {
        super(props);

        if (authenticationService.currentUserValue) { 
            this.props.history.push('/');
        }

        this.state = {
            user:{
                email:"",
                password:"",
                confirmPassword:""
            }
      };
      this.handleChange = this.handleChange.bind(this);
      this.Login = this.Login.bind(this);
    }
