export const FilterBuilder = {
 operation : {
   like:"like",
   equals:"equals",
  },
  properties:{
   filed:"field",
   operation:"operation",
   value:"value"
  },
  sparator:"/",
  generateUri
  

};

function generateUri(filters){
  var filter = "";
  filters.forEach((rows) => {
    filter = filter.concat((filter !== "" ? ",":"")+rows.field+FilterBuilder.sparator+rows.operation+FilterBuilder.sparator+rows.value);
    });
    console.log(filter);
  // return filter;  
}
