import React from 'react';
import axios from 'axios';
import Validate from 'simple-react-validator';

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name:'',
            user:{
                name:String,
                email:String,
                password:String
            }
      };
      this.validator = new Validate();
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event) {

        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
    
        let state = Object.assign({}, this.state);
        state.user[name] = value;
        this.setState(state);
      }
    
    handleSubmit(event) {
        if (this.validator.allValid()) {
            alert('You submitted the form and stuff!');
          } else {
            this.validator.showMessages();
          }
    //     axios.post("http://127.0.0.1:8000/api/register/",
    //     this.state.user
    //     ).then(response => {

    //        alert("Insert Success");
            
    //     }).then((data) => {
    //         this.setState({ users: data })
    //         console.log(this.state.users)
    //     });
    //    event.preventDefault();
    }
    render() {
        return <div className="login">
             <div className="wrapper wrapper-login">
                <div className="container container-signup">
                    <h3 className="text-center">Registrasi</h3>
                    <form onSubmit={this.handleSubmit}>
                        <div className="login-form">
                            <div className="form-group form-floating-label">
                                <input  id="name"
                                name="name" 
                                type="text" 
                                className="form-control input-border-bottom" 
                                onChange={this.handleChange.bind(this)}
                                />
                                {this.validator.message('ss',this.state.name, 'required')}
                                <label htmlFor="name" className="placeholder">Nama</label>
                            </div>
                            
                            {/* <label className="text-danger"> */}
                            {/* {this.validator.message('email', this.state.user.name, 'required|email', 'text-danger')} */}
                            
                            {/* </label> */}
                            
                            <div className="form-group form-floating-label">
                                <input  id="email" 
                                name="email" 
                                type="email" 
                                className="form-control input-border-bottom" 
                                required
                                onChange={this.handleChange.bind(this)}/>
                                <label htmlFor="email" className="placeholder">Email/Username</label>
                            </div>
                            <div className="form-group form-floating-label">
                                <input  id="password" 
                                name="password" 
                                type="password" 
                                className="form-control input-border-bottom"
                                required
                                onChange={this.handleChange.bind(this)}/>
                                <label htmlFor="passwordsignin" className="placeholder">Password</label>
                                <div className="show-password">
                                    <i className="flaticon-interface"></i>
                                </div>
                            </div>
                            <div className="form-group form-floating-label">
                                <input  id="confirmpassword" name="confirmpassword" type="password" className="form-control input-border-bottom" required/>
                                <label htmlFor="confirmpassword" className="placeholder">Confirm Password</label>
                                <div className="show-password">
                                    <i className="flaticon-interface"></i>
                                </div>
                            </div>
                            <div className="row form-sub m-0">
                                <div className="custom-control custom-checkbox">
                                    <input type="checkbox" className="custom-control-input" name="agree" id="agree"/>
                                    <label className="custom-control-label" htmlFor="agree">I Agree the terms and conditions.</label>
                                </div>
                            </div>
                            <div className="form-action">
                                <a href="#" id="show-signin" className="btn btn-danger btn-rounded btn-login mr-3">Cancel</a>
                                <button className="btn btn-primary btn-rounded btn-login">Sign Up</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>;

    }
}

export default Register;
