import {config} from '../config/Config';
import { handleResponse } from './HandleResponseSevice';
import {httpHeader} from '../config/HttpHeader';
import axios from 'axios';


export const httpService = {
    Post,
    Get,
};

function Post(data,urlRoute) {
    
    httpHeader.HeaderRequest();
    return axios.post(config.apiUrl+urlRoute, data)
    .then(handleResponse).then(respose => {
        return respose;
    }).catch(err => {
        console.log(err);
    });
}

function Get(param,urlRoute) {
    return axios.get(config.apiUrl+urlRoute+param)
    .then(handleResponse).then(respose => {
        return respose;
    }).catch(err => {
        console.log(err);
    });
}
