export const FilterBuilder = {
 operation : {
   like:"like",
   equals:"equals",
  },
  properties:{
   filed:"field",
   operation:"operation",
   value:"value"
  },
  generateUri
  

};

function generateUri(filters){
 var filter = "";
 filters.forEach((rows) => {
  filter = filter.concat((filter == "" ? ",":"")+rows.filed+"/"+rows.operation+"/"+rows.value);
  });
  console.log(filter);
}
