import React from 'react';
import { Link } from "react-router-dom";
import {validation} from '../../general/Validation';
import {authenticationService} from '../../service/AutenticationService';

const Error = 
{
    required : "Fild Is Required"
}
class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            user:{
                name:"",
                email:"",
                password:"",
                confirmPassword:""
            },
            Eror:{
                name:{
                    $invalid:true,
                    message:''
                },
                email:{
                    $invalid:true,
                    message:''
                },
                password:{
                    $invalid:true,
                    message:''
                },
                confirmPassword:{
                    $invalid:true,
                    message:''
                }
            }
      };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event) {

        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
    
        let state = Object.assign({}, this.state);
        state.user[name] = value;
        state.Eror[name] = "";
        this.setState(state);
      }
    
    handleSubmit(event) {
        this.validationSubmit();
       // var that = this;
        // event.preventDefault();
        
        // authenticationService.register(this.state.user)
        // .catch(function (error) {
        //         alert(error);
        // });
         event.preventDefault();
    }
    validationSubmit(){
        if(validation.isNull(this.state.user.name)){
          alert();
        }
    }
    render() {
        return <div className="login">
             <div className="wrapper wrapper-login">
                <div className="container container-signup">
                    <h3 className="text-center">Registrasi</h3>
                    <form onSubmit={this.handleSubmit}>
                        <div className="login-form">
                            <div className="form-group form-floating-label">
                                <input  id="name"
                                name="name" 
                                type="text" 
                                className="form-control input-border-bottom" 
                                onChange={this.handleChange.bind(this)}
                                />
                                <label htmlFor="name" className="placeholder">Nama</label>
                            </div>
                            <div className="form-group form-floating-label">
                                <input  id="email" 
                                name="email" 
                                type="text" 
                                className="form-control input-border-bottom"
                                onChange={this.handleChange.bind(this)}/>
                                 <label htmlFor="email" className="placeholder">Email/UserName</label>
                            </div>
                            <div className="form-group form-floating-label">
                                <input  id="password" 
                                name="password" 
                                type="password" 
                                className="form-control input-border-bottom"
                                onChange={this.handleChange.bind(this)}/>
                                <label htmlFor="passwordsignin" className="placeholder">Password</label>
                                <div className="show-password">
                                    <i className="flaticon-interface"></i>
                                </div>
                            </div>
                            <div className="form-group form-floating-label">
                                <input  id="confirmPassword" name="confirmPassword" 
                                type="password"
                                 className="form-control input-border-bottom"  onChange={this.handleChange.bind(this)}/>
                                <label htmlFor="confirmpconfirmPasswordassword" className="placeholder">Confirm Password</label>
                                <div className="show-password">
                                    <i className="flaticon-interface"></i>
                                </div>
                            </div>
                            <div className="form-action">
                            <Link className="btn btn-danger btn-rounded btn-login mr-3" to='/login'>Sign In</Link>
                                <a href="#" id="show-signin" ></a>
                                <button className="btn btn-primary btn-rounded btn-login">Sign Up</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>;

    }
}

export default Register;
